import type { Role } from '@prisma/client'
import 'next-auth'
import 'next-auth/jwt'

declare module 'next-auth' {
  interface Session {
    user: {
      id: string
      name?: string | null
      email?: string | null
      image?: string | null
      role: Role
      employeeId?: string | null
    }
  }

  interface User {
    role?: Role
    employeeId?: string | null
  }
}

declare module 'next-auth/jwt' {
  interface JWT {
    role?: Role
    employeeId?: string | null
  }
}

